<?phpclass PostsController extends AppController {        public $helpers = array('Html', 'Form');    public function index() {    	$allArr = $this->Post->query('SELECT * FROM data_dashboard');    	$arr  = array( );    	foreach ($allArr as $key => $value) {    		$arr[] = $value['data_dashboard'];    	}    	$totalCont = count($allArr);    	$newarray = array(			"sEcho"            => intval("1"),			"iTotalRecords"    => intval($totalCont ),			"iTotalDisplayRecords" => intval( $totalCont ),			"aaData"            => $arr		);        $this->set('posts',json_encode($arr));    }}?>